import React, {useState} from 'react'
import Car from './Car'
import Title from './Title'
import Option from './Option'

// const cars = [
//   { model:'Tesla Model 3', price: 50000, description:'Exterior lights, Autopilot Camera, Front door handle',
//     imagesPath: ['images/tesla-white.jpg', 'images/tesla-black.jpg', 'images/tesla-red.jpg']
//   },
//   { model:'BMW Class9', price: 35000, description:'The new BMW 3 Series G20 is fully exposed thanks to images leaked to the web, mainly in the M Performance version but also in the regular version', imgPath: 'images/bmw.jpg' },
//   { model:'Subaro WRX STI', price: 15000, description:'Subaru has announced that it wont produce an STI variant of its new WRX as the firm turns to electrification', imgPath: 'images/subaro.jpg' }
// ]


export default function Catalog(props) {

  const [choosenCar, setChoosenCar] = useState(0)

  const changeCarDispaly = (event) => setChoosenCar(Number(event.target.value));

  return (
    <div>
        <Title text={"Our cars catalog"} />
        <select onChange={changeCarDispaly}>
          {props.cars.map((car, index) => {
            return <Option text={car.model} index={index}/>
          })}
        </select>
        <Car car={props.cars[choosenCar]} />
    </div>
  )
}


