import { useState } from 'react';
import './App.css';
import Thegame from './components/Thegame';
import Login from './components/Login';
import Endgame from './components/Endgame';


function App() {
  const [page, setPage] = useState("login")
  const [player, setPlayer] = useState({})
  const [endGame, setEndGame] = useState("endGame")

  const savePlayerName = pName => setPlayer({ playerName: pName })

  const changePage = (pageName) => {setPage(pageName)}

  const switchPage = () => {
    switch(page) {
      case "login":
        return <Login savePlayerName={savePlayerName} changePage={changePage} />

      case "game":
        return <Thegame />
        case "endGame":
        return <Endgame/>
    }
  } 

  return (
    <div className="App">
     {switchPage()}
    </div>
  );
}

export default App;
