import './App.css';
import React, {  useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Login from './components/Login';
import Register from './components/Register';
import Admin from './components/Admin';
import Client from './components/Client';

function App() {

//   const [rooms, setRooms] = useState([])

//  const saveNewRoom = (n, c, t) => {
//   let newRoom = { rName: n, rColor: c, rType: t, rDevices: [] }
//   setRooms([...rooms, newRoom])
// }

  const [user, setUser] = useState({})


  const saveUserDetails = (un, p, b) => {
    let newUser = {userN: un, pass: p, balance: b }
    // setUser([...user,newUser])
    setUser(newUser)
  }

  return (
    <div className="App">
    <Router>
         <Routes>
            <Route path='/' element ={<Login user = {user}/>}/>
            <Route path='/register' element ={<Register saveUserDetails = {saveUserDetails} />}/>
            <Route path={'/client/' + user.userN} element ={<Client user = {user}/>}/>
            <Route path='/Admin' element ={<Admin/>}/>
            {/* {'/client/' + user.fName} */}
         </Routes> 
      </Router>
    </div>
  );
}

export default App;
