import React from 'react'
import Manu from './Manu'
import BoardFlyts from './BoardFlyts'

export default function ControlPanel(props) {

    const [table, setTable] = useState("allflyts")
    const changeTable = (t) => {setTable(t)}

    const swichTableView = () => {
        switch(table) {
          case "allflyts":
            return <BoardFlyts flyts={props.flyts}  /> 
    
          case "sortflyts":
            return <BoardFlyts /> 
    
          case "addflyts":
        
            return <BoardFlyts  /> 
    
            case "deleteflyts":
              return <BoardFlyts saveNewTask ={saveNewTask} changepage={changeTable} historyPage={false} />
        }
      }
    
  return (
    <div>
        <Manu/>
        {swichTableView()}   
         </div>
  )
}
