const [workouts, setWorkouts] = useState([])


const generateWorkouts = () => {
  let woArr = []
    for(let i=1; i<=props.user.trainingAmount; i++) {
      let wo = { wNumber: i, km: calcKM(i), isDone: false}
      woArr.push(wo)
    }
    setWorkouts(woArr)
}
 
  useEffect(generateWorkouts,[])

    const calcKM = (wNumber) => {
      return props.user.trainingYears * props.user.trainingAmount * (1.15 ** wNumber);
    }
  
