import React from 'react'
import { useEffect, useState } from 'react';
import Select from './Select'
import Title from './Title';
export default function Login(props) {

    const [numID, setNumID] = useState("") // todo - delete info
    const [fName, setFName] = useState("")
    const [gender, setGender] = useState("")
    
    const genders = ["Male","Female"]

    const getid = (event) => {
        let id = event.target.value
        if(!isNaN(id) && id.length == 9) {
            setNumID(id)
        } else {
            setNumID('')
        }
    }
    const getFullName = (event) => {
        let fullName = event.target.value
        if(fullName.length >= 4) {
            setFName(fullName)
        } else {
            setFName('')
        }
    }


    const getGender = (event) => {
        let Gender = event.target.value
        if(Gender == "Choose Gender") {
            setGender("")
        } else{
            setGender(Gender)
        }
    }

    const isInfoValid = () => numID && fName && gender ? true : false

    const nextPage = () => {
        // save user info iun Home 
        props.changePage("workouts")
        
    }




  return (
    <div>
        <Title text={"Enter your details"}/>
        <input type={'text'} onChange={getid} placeholder={"Enter your id"} /> <br />
        <input type={'text'}  onChange={getFullName} placeholder={"Enter your fullname"} /> <br />
        <Select options={genders} defultOpt={"Choose Gender"} onChangeFn={getGender}/> <br />
        <div hidden={isInfoValid()}>Your info is invalid</div> <br />
        <button disabled={numID &&  fName && gender ? false : true } onClick={nextPage}>Next</button>
    </div>
  )
}
