const getCurrentCondition = () => {
  fetch(`http://dataservice.accuweather.com/currentconditions/v1/215854?apikey=${API_KEY}`)
  .then((res)=>res.json())
  .then((data)=>setCurrentcondition(data))
}

const getCityKey = () => {
  fetch(`http://dataservice.accuweather.com/locations/v1/cities/autocomplete?apikey=${API_KEY}&q=london`)
  .then((res)=>res.json())
  .then((data)=>{
    setCityKey(data[0].Key)
    setCitys(data)
  })
}
