import { useEffect, useState } from 'react';
import './App.css';
import Home from './components/Home';
import Rooms from './components/Rooms';
import RoomName from './components/RoomName';
import Title from './components/Title'
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';

// data from db (get by feach)
const myRooms = [
  { rName: "Ben", rColor: "blueBG", rType: "Bed room", rDevices: [] },
  { rName: "Yoni", rColor: "redBG", rType: "Kitchen", rDevices: [] }
]


function App() {

 const [rooms, setRooms] = useState(myRooms)

 const saveNewRoom = (n, c, t) => {
  let newRoom = { rName: n, rColor: c, rType: t, rDevices: [] }
  setRooms([...rooms, newRoom])
}


useEffect(()=>{
  const saveNewDevice = (device, roomname) => {  
    let tempRooms = rooms;
    tempRooms.forEach(room => {
      if(room.rName == roomname) {
        room.rDevices.push({ dName: device, status: false })
      }
    })  
    setRooms(tempRooms)   
  }  
}, [rooms])



 return (
    <div className="App">
      <Router>
        <Title text={'Smart House'}/>
         <Routes>
            <Route path='/' element={<Home rooms={rooms} />} />
            <Route path='/addroom' element ={<Rooms saveNewRoom = {saveNewRoom}/>}/>
            {rooms.map(room => { // 'room' + room.rName
              return <Route path={`room${room.rName}`} element ={<RoomName saveNewDevice = {saveNewDevice} thisRoom={room}/>}/>
            })}
         </Routes>
      </Router>
    </div>
  );
}

export default App;
