import React from 'react'
import Title from './Title';
import { useState } from 'react';
import Select from './Select';

export default function Devices(props) {

    const [device, setDevice] = useState('')
    const devices = ["Air-Conditioner","Lamp","Stereo system","Hot water switch"]
   
    const getDevices = (event) => {
        let nDevice = event.target.value
        if(nDevice == "Choose Device") {
            setDevice('')
        }else{
            setDevice(nDevice)
        }     
    }
    const addNewDevice = () => {
        props.saveNewDevice(device)
    }
  
  
  
  
  
  
  
    return (
    <div>    
        <Title text={"Devices"}/>
        <Select options={devices} defultOpt={"Choose Device"} onChangeFn={getDevices}/><br />
        <input type={'submit'} onClick={addNewDevice} value={"Add Device"} /><br />
    </div>
  )
}
