import React, {  useEffect, useState } from 'react';
import Forecast from './Forecast';

export default function FiveDays(props) {

  const weekday = ["Sunday","Monday","Tuesday","Wednesday","Thursday","Friday","Saturday"];
  const API_KEY = 'AMn2HUVjVbYVX21t7LFEXOQMHW5cpMlS'

  const [fivedays, setFivedays] = useState([])

  useEffect(()=>{
    fetch(`http://dataservice.accuweather.com/forecasts/v1/daily/5day/${props.cityKey}?apikey=${API_KEY}`)
    .then((res)=>res.json())
    .then((data)=>setFivedays(data.DailyForecasts))
  }, [])

  return (
    <div className='flexboxContainer'>
      <h3>five days forecasts</h3>
      {fivedays.map(f => <Forecast title={weekday[new Date(f.Date).getDay()]} tempaure={f.Temperature.Minimum.Value}/>)}
    </div>
  )
}
