import React from 'react'
import Title from './Title'
import { useState } from 'react';





export default function Rooms(props) {

    const [rName, setRName] = useState('')
    const [rColor, setRColor] = useState('')
    const [rType, setRType] = useState('')
    const [rDevices, setRDevices] = useState('')
    
    
    
        const addNewRoom = () => {
            props.saveNewRoom(rName, rColor, rType, rDevices)
        }
    
  
  
  
  
  
  
  
  
  
    
  
  
  
  
    return (
    <div>
        <Title text={'Smart House'}/>
    <input type={'text'} onChange={setRName} placeholder={"Enter a product name"} /><br />
    <input type={'text'} onChange={setRColor} placeholder={"Enter a product price"} /><br />
    <input type={'text'} onChange={setRType} placeholder={"Enter a image name"} /><br />
    <input type={'text'} onChange={setRDevices} placeholder={"Enter a image name"} /><br />

    <input type={'submit'} onClick={addNewProduct} value={"Add"} /><br />
    </div>
  )
}
