import React from 'react'
import OptionSelect from './OptionSelect'

export default function Select() {

    const options = [
        {text:"--Please choose an option--",value:""},
        {text:"Dog",value:"dog"},
        {text:"Cat",value:"cat"},
        {text:"Hamster",value:"hamster"},
        {text:"Parrot",value:"parrot"},
        {text:"Spider",value:"spider"},
        {text:"Goldfish",value:"goldfish"}
    ]
  return (
    <div>

        <select name="pets" id="pet-select">
            {options.map(option => <OptionSelect value={option.value} text={option.text} />)}
        </select>

    </div>
  )
}
