import React, {  useState } from 'react';
import { useEffect } from 'react';
import FiveDays from './FiveDays'

export default function HomePage(props) {

  // const API_KEY = 'AMn2HUVjVbYVX21t7LFEXOQMHW5cpMlS'
  const API_KEY = 'mf7ATNQVNyhudAntt7XxOxAyaRcVAS3K'
  // const API_KEY = 'WebHzrX0S4ZGR1xqMmgg9s1cMgcjL6CE'
  // mf7ATNQVNyhudAntt7XxOxAyaRcVAS3K
  const condotion = {
    WeatherText: "Mostly sunny",
    Temperature: {
      Metric: {
        Value: 23.5,
      }
    }
  }

  const cityAutocomplete = {
    Key: "215854",
    LocalizedName: "Tel Aviv",
  }

  const [city, setCity] = useState(cityAutocomplete)
  const [cityKey, setCityKey] = useState('')
  const [currentCondtion, setCurrentCondtion] = useState(condotion)

  const favoriteBtn = () => {
    props.saveFavoriteCity(cityKey)
  }

  const fetchCity = (cityName) => {
    fetch(`http://dataservice.accuweather.com/locations/v1/cities/autocomplete?apikey=${API_KEY}&q=${cityName}`)
    .then((res)=>console.log(res.json()) )
    .then((data)=>{
      setCityKey(data[0].Key)
      setCity(data[0])
      // setCities(data) - continue with autocomplete
    })
  }

  const fetchCurrentCondition = (cKey) => {
    // do with useEfect - [cityKey]
    fetch(`http://dataservice.accuweather.com/currentconditions/v1/${cKey}?apikey=${API_KEY}`)
    .then((res)=>res.json())
    .then((data)=>setCurrentCondtion(data[0]))
  }

  useEffect(()=>{
    // fetchCity("tel aviv")
    fetch(`http://dataservice.accuweather.com/locations/v1/cities/autocomplete?apikey=${API_KEY}&q=Tel Aviv}`)
    .then((res)=>console.log(res.json()) )
    .then((data)=>{
      setCityKey(data[0].Key)
      setCity(data[0])
      // setCities(data) - continue with autocomplete
    })
    fetchCurrentCondition(cityKey)
  }, [])


  const getCityAutocomplete = (e) => {
    fetchCity(e.target.value)
    fetchCurrentCondition(cityKey)
  }


  return (
    <div >
      <input type={'text'} onChange={getCityAutocomplete} placeholder={"Search"} />
      <button className='largeBtn' onClick={favoriteBtn}>Add to favorite</button><br />
      <div>{city.LocalizedName}</div>
      <div>{currentCondtion.Temperature.Metric.Value}</div>
      <FiveDays cityKey={cityKey} />
    </div>
  )
}
