import React, {  useEffect, useState } from 'react';
import Table from './Table';
import Manu from './Manu';



export default function TasksPage() {

  const [tasks, setTasks] = useState([])
  const [historyTask, setHistoryTask] = useState([])
  const [table, setTable] = useState("allTasks")

  const changeTable = (t) => {setTable(t)}


  const getAllTasks =() => {
    fetch('/tasks')
    .then(res => res.json())
    .then(data =>setTasks(data))
  }

  useEffect(getAllTasks,[])

  const endTask = (i) => {
    // update history task
    setHistoryTask([...historyTask, tasks[i]])

    // update board tasks by remove task
    let tempTasks = tasks
    tempTasks.splice(i, 1)
    setTasks([...tempTasks])

  // כפתור אנד שולח מידע לכפתור היסטוריה על מנת שהכפתור יציג משימות שבוצעו 
  // כפתור אנד מעדכן את המערך הקיים בפחות משימות
  }
    
  const swichTableView = () => {
    switch(table) {
      case "allTasks":
        return <Table list={tasks} endTask={endTask} histotyPage={false} /> 

      case "historyTask":
        return <Table list={historyTask} endTask={endTask} histotyPage={true} /> 

      case "myTask":
        // filter by user name
        let filteredTaskByName = tasks.filter((tasks)   =>{
          return tasks.tAssigee
        })
        return <Table list={filteredTaskByName} endTask={endTask} histotyPage={false} /> 
    }
  }


  return (
    <div>
       <Manu changeTable={changeTable} />
       {swichTableView()}
       
    </div>
    )
}
