import './App.css';
import React, {  useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Login from './components/Login';
import Register from './components/Register';
import Admin from './components/Admin';
import Client from './components/Client';


function App() {


  const [user, setUser] = useState({})
  const [expenses, setExpenses] = useState([])

  const saveUserDetails = (id , fn, p, b) => {
    let newUser = {id: id, fName: fn, pass: p, balance: b }
    // setUser([...user,newUser])
    setUser(newUser)
  }

  const newExpense = (expense, detail) => {
    setExpenses([...expenses, { expense: expense, detail: detail }])
    setUser({id: user.id, fName: user.fName, pass: user.pass, balance: user.balance - expense })
  }

  return (
    <div className="App">
    <Router>
         <Routes>
            <Route path='/' element ={<Login user = {user}/>}/>
            <Route path='/register' element ={<Register user = {user} saveUserDetails = {saveUserDetails} comp={'reg'} />}/>
            <Route path='/edit' element ={<Register user = {user}  saveUserDetails = {saveUserDetails} comp={'edit'} />}/>
            <Route path={'/client/' + user.fName} element ={<Client user = {user}  newExpense = {newExpense} />}/>
            <Route path='/Admin' element ={<Admin/>}/>
         </Routes> 
      </Router>
    </div>
  );
}

export default App;
