import React, {  useState } from 'react'
import Title from './Title'
import { useNavigate } from 'react-router-dom';


export default function Login(props) {

  const [userName, setUserName] = useState("")
  const [password, setPassword] = useState("")
  const navigate = useNavigate()

  

 const userAndPassValid = ()=> {
 
    if(userName == "ADMIN" && password == "ADMIN") {
        navigate('/Admin/')
    } if(userName == props.user.userN && password == props.user.psss) {
        navigate('client/' + props.user.userN)
    }else {
      alert("check your ditails")
    }

 }

  // const isPassValid = (p) => p != "ADMIN"  ? true : false
  // const isFnValid = (un) => un != "ADMIN" ? true : false



  return (
    <div className='loginbox'>
        <Title text = {"SV-BANK"}/>
        <input type="text" id='user' onChange={(e)=>{setUserName(e.target.value)}} placeholder = {"User Name"} /> <br />
        <input type="password"  id='pass' onChange={(e)=>{setPassword(e.target.value)}} placeholder = {"password"}/> <br />
        <div><a style={{color: "blue"}} href='/register'>create new user</a></div><br />
        <button className='largeBtn' onClick={userAndPassValid}>ENTER</button>
    </div>
  )
}
