import React, {  useState } from 'react';
import HelpBtn from './HelpBtn';
import NameWindow from './NameWindow';

export default function HelpPage(props) {

  const emergencyArr = [{number: 100, symbol: "A"}, {number: 101, symbol: "B"}, {number: 102, symbol: "C"}]

  const [page, setPage] = useState("helpbtn")
  const [emergency, setEmergency] = useState(emergencyArr[1])

  const setEmergencyByIndex = (index) => setEmergency(emergencyArr[index]) 

  const changePage = (pageName) => {setPage(pageName)}

  const switchPage = () => {
    switch (page) {
      case "helpbtn":
        return <HelpBtn/>

      case "window":
        return <NameWindow user = {props.user}/>

  }

}


  return (
    <div className='helpBox'>
      <div>
          <h2 style={{color: 'red'}}>{emergency.number}</h2>
          <select onChange={(e)=>setEmergencyByIndex(e.target.value)}>
            <option value={0}>100 A</option>
            <option value={1}>101 B</option>
            <option value={2}>102 C</option>
          </select>
      </div>
      {switchPage()}
  </div>
  )
}
