import React, {useState, useEffect} from 'react'
import TraningManger from './TraningManger'
import WorkoutPage from './WorkoutPage'

export default function Trainer(props) {
  
  const [page, setPage] = useState("manager")
  const [currentWorkout, setCurrentWorkout] = useState({})
  const [workouts, setWorkouts] = useState([])
  const [wWeek, setWWeek] = useState(1)


  const createWorkouts = () => {
    let woArr = []
    for(let i=1; i<=props.user.trainingAmount; i++) {
      let wo = { wNumber: i, km: calcKM(i-1), isDone: false}
      woArr.push(wo)
    }
    setWorkouts(woArr)
  }

  useEffect(createWorkouts,[])

  const calcKM = (wIndex) => {
    let pressentage = 1.15 ** wWeek
    let res = props.user.trainingYears * 5 / props.user.trainingAmount * (pressentage ** wIndex) 
    return parseFloat(res.toFixed(2))
  }

  const changePage = (pageName) => {setPage(pageName)}
  const thisCurrentWorkout = (workout) => {setCurrentWorkout(workout)}
  const nextWeekCount = () => setWWeek(wWeek + 1)
  
  const nextWeekWorkouts = () => {
    let woArr = workouts
    workouts.forEach((wo)=>{
      wo.isDone = false
      let newKM = wo.km * 1.15
      wo.km = newKM.toFixed(2)
    })
    setWorkouts(woArr)
  }

  const workoutIsDone = (wNumber) => {
    let tempWorkouts = workouts
    tempWorkouts[wNumber - 1].isDone = true
    setWorkouts([...tempWorkouts])
  }

  const opneWorkoutPage = () => {
    switch(page) {
      case "manager":
        return <TraningManger nextWeekWorkouts={nextWeekWorkouts} createWorkouts={createWorkouts} workouts={workouts} nextWeekCount={nextWeekCount} thisCurrentWorkout={thisCurrentWorkout} changePage={changePage}/>
      case "workout":
        return <WorkoutPage currentWorkout={currentWorkout} workoutIsDone={workoutIsDone} changePage={changePage} />
    }
  }
 
  return (
    <div>
      {opneWorkoutPage()}    
    </div>
  )
}
