import React from 'react'
import Title from './Title'
import { useState } from 'react';





export default function Rooms(props) {

    const [rName, setRName] = useState('')
    const [rColor, setRColor] = useState('')
    const [rType, setRType] = useState('')
    const [rDevices, setRDevices] = useState('')
    
    
    
        const addNewRoom = () => {
            props.saveNewRoom(rName, rColor, rType, rDevices)
        }
    

    return (
    <div>
        <Title text={'Smart House'}/>
    <input type={'text'} onChange={setRName} placeholder={"Enter a room name"} /><br />
    <input type={'text'} onChange={setRColor} placeholder={"Enter a color"} /><br />
    <input type={'text'} onChange={setRType} placeholder={"Enter type room"} /><br />
    <input type={'text'} onChange={setRDevices} placeholder={"Enter device"} /><br />

    <input type={'submit'} onClick={addNewRoom} value={"Add"} /><br />
    </div>
  )
}
