import React, {  useState, useEffect } from 'react'
import { useNavigate } from 'react-router-dom';
import Title from './Title'


export default function Register(props) {

const [id, setId] = useState("")
const [fullName, setFullName] = useState("")
const [password, setPassword] = useState("")
const [confirmPass, setConfirmPass] = useState("")
const [money, setMoney] = useState("")


const navigate = useNavigate()

useEffect(()=>{
  if(props.comp == "edit") {
    setId(props.user.id)
    setFullName(props.user.fName)
    setPassword(props.user.pass)
    setConfirmPass(props.user.confirmPass)
    setMoney(props.user.balance)

  }
}, [])


const createUser = () => {
  if (id.length != 9||fullName.length < 4 ) {
    console.log("no-good")
  } else if(password.length < 6 || password != confirmPass || money < 0 || money == "") {
console.log("not valid");
  }else {
    props.saveUserDetails( id,fullName, password, confirmPass, money)
    navigate('/client/' + fullName)
  }

  

}

  return (
    <div className='loginbox'>
      <Title text = {"REGISTER"}/>
        <input type="number" className='inputt' onChange={(e)=>{setId(e.target.value)}} placeholder = {"ID"} /> <br />
        <input type="text"  className='inputt' value={fullName} onChange={(e)=>{setFullName(e.target.value)}} placeholder = {"Full-Name"}/> <br />
        <input type="password"  className='inputt' onChange={(e)=>{setPassword(e.target.value)}} placeholder = {"Password"}/> <br />
        <input type="password"  className='inputt' onChange={(e)=>{setConfirmPass(e.target.value)}} placeholder = {"Confirm-Password"}/> <br />
        <input type="number"  className='inputt' onChange={(e)=>{setMoney(e.target.value)}} placeholder = {"Money"}/> <br />
        <button className='largeBtn' onClick={createUser}>{props.comp == "reg" ? "Create" : "Edit"}</button>
    </div>
  )
}
