import React, {useState} from 'react'
import Title from './Title'
import { useNavigate } from 'react-router-dom';



export default function Client(props) {

    const [showAction, setShowAction] = useState(false)
    const [expenses, setExpenses] = useState("")
    const [DetailOfExp, setDetailOfExp] = useState("")

    const navigate = useNavigate()
    const toLogin = () => {
        navigate('/')
    }
    const balance = () => {
        alert("your balance:" + " " + props.user.balance)
    }


   const confirmExpenses = () => {
      props.saveUserDetails(expenses)
   }
  return (
    <div>
       <Title text = {"Welcome:" +" "+ props.user.userN}/>
        <button className='largeBtn' onClick={balance}>Balance</button>
        <button className='largeBtn' onClick={()=>setShowAction(!showAction)}>Action</button> <br />
        <button className='largeBtn' onClick={()=>navigate('/')}>Exit</button>
        <button className='largeBtn' onClick={()=>navigate('/edit')}>Edit</button><br />
        <div><button className='largeBtn' onClick={toLogin}>to-login</button></div>
        <div className='margin-10' style={{visibility: showAction ? 'visible' : 'hidden' }}>
            <input type="text" onChange={(e)=>{setDetailOfExp(e.target.value)}} placeholder={"detail"} /> <br />
            <input type="number" onChange={(e)=>{setExpenses(e.target.value)}} placeholder={"expenses"} /><br />
            <button className='largeBtn' onClick={confirmExpenses}>Confirm</button>
        </div>

    </div>

  )
}
