import React,  { useState } from 'react'
import { useNavigate } from 'react-router-dom';
import ChooseDevice from './ChooseDevice';
import DeviceItem from './DeviceItem';


export default function RoomName(props) {

    const navigate = useNavigate()

    const [deviceCompFlag, setDeviceCompFlag] = useState(false)

    const showAddDeviceComp = ()=>{
        if(deviceCompFlag){
            return <ChooseDevice saveNewDevice={props.saveNewDevice} roomname={props.thisRoom.rName}/>
        }
    }
    
   

    // 1
    // add button - save the choones device on false stuts in room list
    // please check out the validation rules
    // dispaly the dvice as component (like room item)

    // 2
    // create function that changes device status (on/off) from true/false
    // chagne by this status the BG color - in the comp use room as props and if/else to change the className(hook) 

    return (
    <div>    
        <div>Room Name: {props.thisRoom.rName}</div>
        <div>Room Type: {props.thisRoom.rType}</div>
        
        {props.thisRoom.rDevices.map(dev => <DeviceItem device={dev} />)}
        {showAddDeviceComp()}
        <input type={'submit'} onClick={()=>setDeviceCompFlag(!deviceCompFlag)} value={"Devices"} /><br />
        <button onClick={()=>navigate('/')}>Home</button>
    </div>
  )
}
