import React, {useState} from 'react'
import TaskWindow from './TaskWindow'



export default function Table(props) {

    const [currentTask, setCurrentTask] = useState(undefined)
    const [indexTask, setIndexTask] = useState(undefined)

    const currentTaskWindow = () => {
        if(currentTask && !props.historyPage) {
            return <TaskWindow endTask={props.endTask} index={indexTask} task={currentTask} setCurrentTask={setCurrentTask} />
        }
    }

    const updateCurrentTask = (task, index) => {
        setCurrentTask(task)
        setIndexTask(index)
    }

    const deleteTask = (taskName) => {
        fetch('/deleteTask', {
            method: 'POST',
            headers: {
            'content-type':'application/json'
            },
            body: JSON.stringify({ tName: taskName })
        })
        .then((response)=> { return response.json() })
        .then((res)=>{
          if(res.isTaskDeleted) {
            alert("Task Deleted")
            console.log(res.info)
            props.changepage('allTasks')
          }
        })
    }

  return (
    <div>
        <table className='table'>
            {props.list.map((task, index) => {
                return (
                    <tr className='tr' onClick={()=>updateCurrentTask(task, index)} > 
                        <td>{task.tName}</td>
                        <td>{task.tAssigee}</td>
                        <td><button>X</button></td>
                    </tr>
                )
            })}
        </table>
        {currentTaskWindow()}
    </div>
  )
}
