import React, {useState} from 'react'
import Title from './Title'

export default function AddAddProduct(props) {

    const [productName, setProductName] = useState('')
    const [price, setPrice] = useState('')
    const [imagePath, setImagePath] = useState('')

    const getProductName = (event) => {
        let productN = event.target.value
        if(productN.length >= 2) {
            setProductName(productN)
        }
    }

    const getProductPrice = (event) => {
        let price = event.target.value
        if(price > 0) {
            setPrice(price)
        } 
    }

    const getImgName = (event) => {
        let imgName = event.target.value
        if(imgName) {
            setImagePath(`images/${imgName}.jpg`)
        } 
    }

    const addNewProduct = () => {
        props.saveNewProductsDetails(productName, price, imagePath)
    }


  return (
    <div>
        <Title text={'Add a new Kinder product'}/>
        <input type={'text'} onChange={getProductName} placeholder={"Enter a product name"} /><br />
        <input type={'text'} onChange={getProductPrice} placeholder={"Enter a product price"} /><br />
        <input type={'text'} onChange={getImgName} placeholder={"Enter a image name"} /><br />
        <input type={'submit'} onClick={addNewProduct} value={"Add"} /><br />
    </div>
  )
}
