import { useState } from 'react';
import './App.css';
import GamePage from './componnets/GamePage';
import HomePage from './componnets/HomePage';
import EndPage from './componnets/EndPage';


const pages = {
  home: 'home',
  game: 'game',
  end: 'end'
}

function App() {

  const [player, setPlayer] = useState({})
  const [currentPage, setCurrentPage] = useState(pages.home)

  const savePlayerName = pName => setPlayer({ playerName: pName })

  const changePage = page => setCurrentPage(page)

  const displayPage = () => {
    switch(currentPage) {
      case pages.home:
        return <HomePage savePlayerName={savePlayerName} changePage={changePage} />
      case pages.game:
        return <GamePage playerName={player.playerName} changePage={changePage}/>
      case pages.end:
        return <EndPage changePage={changePage}/>
    }
  }

  return (
    <div className="App">
      {displayPage()}
    </div>
  );
}

export default App;
