import React, {useState} from 'react'
import Title from './Title'

export default function HomePage(props) {
    
    const [name, setName] = useState('')
    const [inputStyle, setInputStyle] = useState('')

    const isNameValid = e => {
        let input = e.target.value; 
        if(input.length > 0) {
            setName(input)
            setInputStyle('greenBG')
        } else {
            setName('')
            setInputStyle('redBG')
        }
    }
   
    const startGame = () => {
        if(name) {
            props.savePlayerName(name)
            props.changePage("game")
        }
        else {
            alert("Please enter your name")
        }
    }

    return (
    <div>
        <Title text={"Reday for WAR?!"} />
        <input type="text" className={inputStyle} onChange={isNameValid} placeholder='Enter your name' /><br />
        <input type="submit" onClick={startGame} value="GO!" />
    </div>
  )
}
