import React from 'react'
import Title from './Title'
import { useState } from 'react';

export default function Login(props) {
    const [pName, setPName] = useState("")
    const [err, setErr] = useState('')
    
    const getPlayerName = (event) => {
        let playerN = event.target.value
        if(playerN.length >= 3) {
            setPName(playerN)
            setErr("")
        } else {
            setErr("name need to be more then 2 letter")
        }
    }


    const addPlayer = () => {
        if(err) {
            alert("Please check your name")
        } else {
            props.savePlayerName(pName)
            props.changePage()    
        }
    }
    
  return (
    <div>
    <Title text={"raedy for war"} />
    <input type={"text"} onChange={getPlayerName} placeholder={'Enter Your Name'}/><br></br>
    <div>{err}</div>

    <button onClick={addPlayer}>start</button>    
    
    </div>
  )
}
