import React, {  useEffect, useState } from 'react';
import Table from './Table';
import Manu from './Manu';



export default function TasksPage() {

  const [tasks, setTasks] = useState([])
  const [historyTask, setHistoryTask] = useState("")

  const getAllTasks =() => {
    fetch('/tasks')
    .then(res => res.json())
    .then(data =>setTasks(data))
  }

  useEffect(getAllTasks,[])

const endTask = (index) => {
  // update history task
  // 
tasks[index]
let history = [];
 index = 0
 setHistoryTask([...history, tasks[index]])
 let tempTasks = tasks
tempTasks.splice(index, 1)
setTasks(tempTasks)

}



  return (
    <div>
       <Manu/>
       <Table list={tasks} endTask={endTask} historyTask={historyTask}/> 
       
    </div>
    )
}
