import './App.css';
import React, {  useState, useEffect } from 'react';
// import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Title from './components/Title';
import Movies from './components/Movies';

function App() {

  const [movies, setMovies] = useState([])
  const API_KEY = '64aa1c3f'
  const DOMAIN = 'omdbapi.com'
  const moviesID = ['tt3480822', 'tt6443346', 'tt1825683', 'tt4154796'] 

  useEffect(()=>{
    if(!movies.length > 0) {
      let arrRes = []
      moviesID.forEach((mid)=>{
        fetch(`https://www.${DOMAIN}/?i=${mid}&apikey=${API_KEY}`)
        .then((res)=>res.json())
        .then((data)=>arrRes.push(data))
      })
      setMovies(arrRes)
    }
  }, [])


  return (
    <div className="App">
    <Title text = {"Movie Rent"}/>
    {movies}
    <Movies/>
    </div>
  );
}

export default App;
