import React, {useState} from 'react'
import { useNavigate } from 'react-router-dom';
import Login from './Login';
import WorkoutSetup from './WorkoutSetup';
import Ready from './Ready';
import WorkoutPage from './WorkoutPage';

export default function Home(props) {
  
    const navigate = useNavigate()

    const [user, setUser] = useState({})
    const [page, setPage] = useState("login")
    
    const saveUserDetails = (i, fn, g) => {
      setUser({ id: i, fName: fn, gender: g })
    }

    const saveTrainingDetails = (tAmount, tYears) => {
      setUser({ id: user.id, fName: user.fName, gender: user.gender, trainingAmount: tAmount ,trainingYears: tYears})
    }

    const goToTraningPage = () => {
      props.addUser(user)
      // navigate('/trainer/' + user.fName)
      navigate(`/trainer/${user.fName}`)
    }

    const changePage = (pageName) => {setPage(pageName)}
  
    const switchPage = () => {
      switch(page) {
        case "login":
          return <Login saveUserDetails = {saveUserDetails} changePage={changePage} />
        case "workouts":
          return <WorkoutSetup saveTrainingDetails = {saveTrainingDetails} changePage={changePage} />
        case "ready":
          return <Ready saveUserDetails = {saveUserDetails} changePage={changePage} goToTraningPage={goToTraningPage}/>
        
      }
    } 
  
    return (
    <div>
        {switchPage()}
    </div>
  )
}
