import React, { useState } from 'react';
import Title from './Title'



export default function NewTask(props) {

const [taskName, setTaskName] = useState("")
const [taskDescription, setTaskDescription] = useState("")
const [taskAssigee, setTaskAssigee] = useState("")

    const addNewTask = () => {
        props.saveNewTask(taskName, taskDescription, taskAssigee)
        props.changepage('allTasks')
    }

  return (
    <div className='newtask'>
        <Title text = {"Create A New Task"}/>
        <input type={'text'} onChange={(e)=>{setTaskName(e.target.value)}} placeholder={"task name"} /> <br />
        <input type={'text'}  onChange={(e)=>{setTaskDescription(e.target.value)}} placeholder={"task description"} /> <br />
        <input type={'text'}  onChange={(e)=>{setTaskAssigee(e.target.value)}} placeholder={"task assigee"} /> <br />
        <button className='largeBtn' onClick={addNewTask}>Add</button>
    </div>
  )
}
