import { useState } from 'react';
import './App.css';
import Thegame from './components/Thegame';
import Login from './components/Login';
import Endgame from './components/Endgame';


function App() {
  const [page, setPage] = useState("login")
  const [player, setPlayer] = useState({})

  const savePlayerName = pName => setPlayer({ playerName: pName, win: 0,  lose: 0, lastGameStatus: '' })

  const saveWinOrLose = isWin => { 
    if(isWin) {
      setPlayer({ 
        playerName: player.playerName,
        win: player.win + 1,
        lose: player.lose,
        lastGameStatus: 'winner'
      })
    } else {
      setPlayer({ 
        playerName: player.playerName,
        win: player.win,
        lose: player.lose + 1,
        lastGameStatus: 'loser'
      })
    }
    changePage('end')
  }

  const changePage = (pageName) => {setPage(pageName)}

  const switchPage = () => {
    switch(page) {
      case "login":
        return <Login savePlayerName={savePlayerName} changePage={changePage} />

      case "game":
        return <Thegame saveWinOrLose={saveWinOrLose} player={player} />

      case "end":
        return <Endgame player={player} />
    }
  } 

  return (
    <div className="App">
     {switchPage()}
    </div>
  );
}

export default App;
