import React, {  useState } from 'react';
import FiveDays from './FiveDays'

export default function HomePage(props) {

  const API_KEY = 	'AMn2HUVjVbYVX21t7LFEXOQMHW5cpMlS'

  const [city, setCity] = useState({})
  const [cityKey, setCityKey] = useState('') // 215854
  const [currentcondition, setCurrentcondition] = useState({})

  const favoriteBtn = (city,temperature)=> {
    props.saveFavoriteCity(city,temperature)
  }

  const getCityAutocomplete = (e) => {
      fetch(`http://dataservice.accuweather.com/locations/v1/cities/autocomplete?apikey=${API_KEY}&q=${e.target.value}`)
      // fetch(`http://dataservice.accuweather.com/locations/v1/cities/autocomplete?apikey=WebHzrX0S4ZGR1xqMmgg9s1cMgcjL6CE&q=tel`)
      .then((res)=>res.json())
      .then((data)=>{
        setCityKey(data[0].Key)
        setCity(data[0])
        // setCities(data) - continue with autocomplete
      })

      // do with useEfect - [cityKey]
      fetch(`http://dataservice.accuweather.com/currentconditions/v1/${cityKey}?apikey=${API_KEY}`)
      .then((res)=>res.json())
      .then((data)=>setCurrentcondition(data[0]))
  }


  return (
    <div >
      <input type={'text'} onChange={getCityAutocomplete} placeholder={"Search"} />
      <button className='largeBtn' onClick={favoriteBtn}>Add to favorite</button><br />
      <div>{city.LocalizedName}</div>
      <div>{currentcondition.Temperature.Metric.Value}</div>
      <FiveDays />
    </div>
  )
}
