import React from 'react'
import Manu from './Manu'
import BoardFlyts from './BoardFlyts'

export default function ControlPanel() {

    const [table, setTable] = useState("allflyts")
    const changeTable = (t) => {setTable(t)}

    const swichTableView = () => {
        switch(table) {
          case "allflyts":
            return <BoardFlyts list={tasks} endTask={endTask} historyPage={false} /> 
    
          case "sortflyts":
            return <BoardFlyts list={historyTask} endTask={endTask} historyPage={true} /> 
    
          case "addflyts":
            // filter by user name
            let filteredTaskByName = tasks.filter(thisTask =>{
              return thisTask.tAssigee === props.user
            })
            return <BoardFlyts list={filteredTaskByName} endTask={endTask} historyPage={false} /> 
    
            case "deleteflyts":
              return <BoardFlyts saveNewTask ={saveNewTask} changepage={changeTable} historyPage={false} />
        }
      }
    
  return (
    <div>
        <Manu/>
        {swichTableView()}   
         </div>
  )
}
