import React, {  useEffect, useState } from 'react';
import Table from './Table';
import Manu from './Manu';
import NewTask from './NewTask';


export default function TasksPage(props) {

  const [tasks, setTasks] = useState([])
  const [historyTask, setHistoryTask] = useState([])
  const [table, setTable] = useState("allTasks")
  const [users, setUsers] = useState([])
  
  
  const changeTable = (t) => {setTable(t)}

  const getAllUsers = () => {
    fetch('/users')
    .then(res => res.json())
    .then(data =>setUsers(data))
  }
  useEffect(getAllUsers,[])

  const getAllTasks =() => {
    fetch('/tasks')
    .then(res => res.json())
    .then(data =>setTasks(data))
  }

  useEffect(getAllTasks,[])

  const endTask = (i) => {
    // update history task
    setHistoryTask([...historyTask, tasks[i]])

    // update board tasks by remove task
    let tempTasks = tasks
    tempTasks.splice(i, 1)
    setTasks([...tempTasks])

  // כפתור אנד שולח מידע לכפתור היסטוריה על מנת שהכפתור יציג משימות שבוצעו 
  // כפתור אנד מעדכן את המערך הקיים בפחות משימות
  }
  const saveNewTask = (n, d, a) => {
    let newTask = { tName: n, tDescription: d, tAssigee:a }
    setTasks([...tasks, newTask])
  }
  const swichTableView = () => {
    switch(table) {
      case "allTasks":
        return <Table list={tasks} endTask={endTask} historyPage={false} /> 

      case "historyTask":
        return <Table list={historyTask} endTask={endTask} historyPage={true} /> 

      case "myTask":
        // filter by user name
        let filteredTaskByName = tasks.filter(thisTask =>{
          return thisTask.tAssigee === props.user
        })
        return <Table list={filteredTaskByName} endTask={endTask} historyPage={false} /> 

        case "newTask":
          return <NewTask saveNewTask ={saveNewTask} changepage={changeTable} historyPage={false} />
    }
  }

  
  return (
    <div>
       <Manu changeTable={changeTable} />
       {swichTableView()}
       
    </div>
    )
}
