import React from 'react'
import Title from './Title'
import { useState } from 'react';
import { useNavigate } from 'react-router-dom';





export default function Rooms(props) {
    const navigate = useNavigate()


    const [roomName, setRoomName] = useState('')
    const [roomColor, setRoomColor] = useState('')
    const [roomType, setRoomType] = useState('')
    // const [device, setDevice] = useState('')
    
    const backToHomePage = () => {
        navigate('/')
        props.saveNewRoom(roomName, roomColor, roomType)
}


    const getRName = (event) => {
        let rName = event.target.value
            setRoomName(rName)
        
    }

    const getRColor = (event) => {
        let rColor = event.target.value
            setRoomColor(rColor)
         
    }

    const getRType = (event) => {
        let rType = event.target.value
            setRoomType(rType)
        
    }
    // const getDevice = (event) => {
    //     let nDevice = event.target.value
    //     setDevice(nDevice)
        
    // }


    
    
        const addNewRoom = () => {
            props.saveNewRoom(roomName, roomColor, roomType)
            
        }
    

    return (
    <div>
        <Title text={'Smart House'}/>
        <input type={'text'} onChange={getRName} placeholder={"Enter a room name"} /><br />
        <input type={'text'} onChange={getRColor} placeholder={"Enter a color"} /><br />
        <input type={'text'} onChange={getRType} placeholder={"Enter type room"} /><br />
        <input type={'submit'} onClick={backToHomePage} value={"Add"} /><br />
    </div>
  )
}
