import React, {useState, useEffect} from 'react'

export default function DeviceItem(props) {

    const [styleDevice, setStyleDevice] = useState('')
    const [iDevcie, setIDevcie] = useState(props.device)

    const deviceOn = () =>{
        let dev = iDevcie
        dev.status = !iDevcie.status
    }

    // className={`roomItem ${props.device.rColor}`}  onClick={goToRoomNameComp}

    useEffect(() => {
        if(props.device.status) {
            setStyleDevice('greenBG')
        } else {
            setStyleDevice('redBG')
        }
    }, [styleDevice]) 


    return (
        <div >
            <div className={`deviceItem ${styleDevice}`} onClick={deviceOn}>{props.device.dName} </div>
            {/* <div className={`deviceItem ${props.device.status ? 'greenBG' : 'redBG'}`} onClick={deviceOn}>{props.device.dName} </div> */}
        </div>
  )
}
