import React,  { useState } from 'react'
import { useNavigate } from 'react-router-dom';
import ChooseDevice from './ChooseDevice';
import Title from './Title';

export default function Devices(props) {

    const navigate = useNavigate()

    const [deviceCompFlag, setDeviceCompFlag] = useState(false)

    const showAddDeviceComp = ()=>{
        if(deviceCompFlag){
            return <ChooseDevice/>
        }
    }

    // 1
    // add button - save the choones device on false stuts in room list
    // please check out the validation rules
    // dispaly the dvice as component (like room item)

    // 2
    // create function that changes device status (on/off) from true/false
    // chagne by this status the BG color - use room as props and if/else to change the className 

    return (
    <div>    
        <Title text={'Devices'}/>
        {/* rName, rType */}
        {showAddDeviceComp()}
        <input type={'submit'} onClick={()=>setDeviceCompFlag(!deviceCompFlag)} value={"Add Device"} /><br />
        <button onClick={()=>navigate('/')}>Home</button>
    </div>
  )
}
