import React from 'react'

export default function TaskWindow(props) {

    const closeWindow = () => {
        props.setCurrentTask(undefined)
    }

    const endTaskWindow = ()=> {
        props.endTask(props.index)
        closeWindow()
    }


  return (
      <div style={{border: "solid 1px black", margin:"10px"}}>
        <div>{props.task.tName}</div>
        <div>{props.task.tDescription}</div>
        <div>{props.task.tAssigee} </div>
        <button className='roomBtm' onClick={closeWindow}>Close</button>
        <button className='roomBtm' onClick={endTaskWindow}>End</button>
        
    </div>
  )
}
