import { useState } from 'react';
import './App.css';
import Header from './Components/Header';
import Catalog from './Components/Catalog';
import Option from './Components/Option';
import AddCar from './Components/AddCar';

// db data
const carsArr = [
  { model:'Tesla Model 3', price: 50000, description:'Exterior lights, Autopilot Camera, Front door handle',
    imagesPath: ['images/tesla-white.jpg', 'images/tesla-black.jpg', 'images/tesla-red.jpg']
  },
  { model:'BMW Class9', price: 35000, description:'The new BMW 3 Series G20 is fully exposed thanks to images leaked to the web, mainly in the M Performance version but also in the regular version', imagesPath: ['images/bmw.jpg'] },
  { model:'Subaro WRX STI', price: 15000, description:'Subaru has announced that it wont produce an STI variant of its new WRX as the firm turns to electrification', imagesPath: ['images/subaro.jpg'] }
]

function App() {

  // hook
  const [currentComp, setCurrentComp] = useState("catalog")
  const [cars, setCars] = useState(carsArr)


  const saveNewCarDetails = (m, p, path) => {
    let newCar = { model: m, price: p, imagesPath: [path] }
    setCars([...cars, newCar])
  }

  const showComp = () => {
    if(currentComp == "catalog") {
      return <Catalog cars={cars} />
    } else {
      return <AddCar saveNewCarDetails={saveNewCarDetails}/>
    }
  }


  return (
    <div className="App">
      <Header />
      <button onClick={()=>setCurrentComp("catalog")}>Catalog</button>
      <button onClick={()=>setCurrentComp("addCar")}>Add Car</button>
      {showComp()}
    </div>
  );
}

export default App;
