import { useState } from 'react';
import './App.css';
import Gamepage from './components/Gamepage';
import Homepage from './components/Homepage';
import Endpage from './components/Endpage';

const pages = {
  home: 'home',
  game: 'game',
  end: 'end'
}

function App() {
  const [currentComp, setCurrentComp] = useState("Products")
  const [player, setPlayer] = useState({})
  const [currentPage, setCurrentPage] = useState(pages.home)

  const savePlayerName = pName => setPlayer({ playerName: pName })

  const changePage = page => setCurrentPage(page)

  const displayPage = () => {
    switch(currentPage) {
      case pages.home:
        return <Homepage savePlayerName={savePlayerName} changePage={changePage} />
      case pages.game:
        return <Gamepage playerName={player.playerName} changePage={changePage}/>
      case pages.end:
        return <Endpage changePage={changePage}/>
