import { useState } from 'react';
import './App.css';
import Thegame from './components/Thegame';
import Login from './components/Login';
import Endgame from './components/Endgame';


function App() {
  const [page, setPage] = useState("login")
  const [player, setPlayer] = useState({})

  const savePlayerName = pName => setPlayer({ playerName: pName })

  const saveWinOrLose = winner => { 
    setPlayer({ playerName: pName })
  }

  const changePage = (pageName) => {setPage(pageName)}

  const switchPage = () => {
    switch(page) {
      case "login":
        return <Login savePlayerName={savePlayerName} changePage={changePage} />

      case "game":
        return <Thegame changePage={changePage} player={player} />

      case "end":
        return <Endgame changePage={changePage} />
    }
  } 

  return (
    <div className="App">
     {switchPage()}
    </div>
  );
}

export default App;
