import React, {useState} from 'react'
import Title from './Title'

export default function Thegame(props) {

  // add the next hooks - player and computer: deck, cardIndex, score
  const [compDeck, setCompDeck] = useState([])
  const [playerDeck, setPlayerDeck] = useState([])
  const [cardIndex, setCardIndex] = useState(0)
  const [gameScore, setGameScore] = useState({ player: 0, comp: 0})


  // fucntions - createDecks, nextCard, checkRoundWin, gameWinner
  const createDecks = () => {
    // loop generate 52 cards in array
    let cardArr = [];
    for(let card=1; card<=13; card++) {
      for(let i=0; i<4; i++) {
        cardArr.push(card)
      }
    }
    console.log(cardArr);
    // for(let i=1, j=1, card=1; i<=52; i++, j++) {
      //   cardArr.push(card)
      //   if(j == 4) {
        //     j = 0
        //     card++
        //   }
        // }
        
        let pCards = []
        let cCards = []
        let rnd;
        while(cardArr.length > 0) {
          rnd = Math.floor(Math.random() * cardArr.length)
          pCards.push(cardArr[rnd])
          cardArr.splice(rnd, 1)
          
          rnd = Math.floor(Math.random() * cardArr.length)
          cCards.push(cardArr[rnd])
          cardArr.splice(rnd, 1) 
        }
        
        setCompDeck(cCards)
        setPlayerDeck(pCards)
        // setPlayerScore(cCards(rnd)+1)
        // setCompScore.map(cCards.push(cardArr[rnd]))


        console.log(pCards);
        console.log(cCards);
    // מטרת הפוקציה היא לחלק 26 קלפים רנדומליים לכל שחקן
    // שתי מערכים של 26 מספרים רנדומלים, אחד לכל שחקן

    // ממה נתחיל?
    // אני אצור מערך ריק
    // לתוך מערך זה אני אכניס מספרים מ1עד 13 בכפולות של 4
    // לאחר מכן אני אוציא את המספרים באופן אקראי 
    // ואחלק בין שתי מערכים נוספים
    // 2 מערכים שלהם אוכל לחלק א 26 הקלפים
    // 
    // split to two decks array randomly (split, random, push)

    // set on hooks
  }
   
  const hitButton = () => {
      setCardIndex(cardIndex + 1)
      if(compDeck[cardIndex] < playerDeck[cardIndex]) {
        // palyer + 1
        // comp 
        setGameScore({ player: gameScore.player + 1, comp: gameScore.comp })
      } else {
          setGameScore({comp: gameScore.comp + 1, player: gameScore.player})
      }
     

     }

  // hit
  // hit button the to move the index by one - hook for the current index
  // display the cards
  
  // calc score - who has greather card nuimber and update a hook with his score
// כשלוחצים על כפתור היט אני רוצה לשמור את התוצאה  של אותו הסיבוב
  // display the score in the game  
// when all cards are finished we need to compare the comp-palyes scores

  // display end game with win/lose title and player name

  return (
    <div>
      <Title text={"WAR!"} />
      <div>Round: {cardIndex+1}</div>
      <div>Comp</div>
      <div>{compDeck[cardIndex]}</div>
      <div>Player</div>
      <div>{playerDeck[cardIndex]}</div>
      
      
      <button onClick={hitButton}>Hit</button>
      <button onClick={createDecks}>Deck</button> <br /><br />
    
      <div>ScoreBoard:</div>
      <div>     player: {gameScore.player}-VS-{gameScore.comp} :comp     </div>
    </div>
  )
}
