import React, {useState} from 'react'
import Title from './Title'

export default function AddCar(props) {

    const [model, setModel] = useState('')
    const [price, setPrice] = useState('')
    const [imagePath, setImagePath] = useState('')

    const getCarModel = (event) => {
        let model = event.target.value
        if(model.length >= 2) {
            setModel(model)
        }
    }

    const getCarPrice = (event) => {
        let price = event.target.value
        if(price > 0) {
            setPrice(price)
        } 
    }

    const getImgName = (event) => {
        let imgName = event.target.value
        if(imgName) {
            setImagePath(`images/${imgName}.jpg`)
        } 
    }

    const addNewCar = () => {
        props.saveNewCarDetails(model, price, imagePath)
    }


  return (
    <div>
        <Title text={'Add New Car'}/>
        <input type={'text'} onChange={getCarModel} placeholder={"Enter a car model"} /><br />
        <input type={'text'} onChange={getCarPrice} placeholder={"Enter a car price"} /><br />
        <input type={'text'} onChange={getImgName} placeholder={"Enter a image name"} /><br />
        <input type={'submit'} onClick={addNewCar} value={"Add"} /><br />
    </div>
  )
}
