import { useState } from 'react';
import './App.css';
import Home from './components/Home';
import Rooms from './components/Rooms';
import Devices from './components/RoomName';
import Title from './components/Title'
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';

// data from db (get by feach)
const myRooms = [
  { rName: "Ben", rColor: "blueBG", rType: "Bed room", rDevices: [] },
  { rName: "Yoni", rColor: "redBG", rType: "Bed room", rDevices: [] }
]


function App() {

 const [rooms, setRooms] = useState(myRooms)
 const [device, setDevice] = useState(myRooms[{rDevices:[]}])

 const saveNewRoom = (n, c, t) => {
  let newRoom = { rName: n, rColor: c, rType: t, rDevices: [] }
  setRooms([...rooms, newRoom])
}
const saveNewDevice = (d) => {
  let newDevice = {rDevices: d}
  setDevice([...device,newDevice])
}

// fn - add new device




 return (
    <div className="App">
      <Router>
        <Title text={'Smart House'}/>
         <Routes>
            <Route path='/' element={<Home rooms={rooms} />} />
            <Route path='/addroom' element ={<Rooms saveNewRoom = {saveNewRoom}/>}/>
            <Route path='/roomname' element ={<RoomName saveNewDevice = {saveNewDevice}/>}/>
         </Routes>
      </Router>
    </div>
  );
}

export default App;
