import React, {useState, useEffect} from 'react'
import Title from './Title'
import WorkoutItem from './WorkoutItem'

export default function TraningManger(props) {

  const [workouts, setWorkouts] = useState([])
  const createWorkouts = () => {
    let woArr = []
      for(let i=1; i<=props.user.trainingAmount; i++) {
        let wo = { wNumber: i, km: calcKM(i-1), isDone: false}
        woArr.push(wo)
      }
      setWorkouts(woArr)
  }
   
  useEffect(createWorkouts,[])
  
  const calcKM = (wNumber) => {
    let res = props.user.trainingYears * 5 / props.user.trainingAmount * (1.15 ** wNumber) 
    return  res.toFixed(2)
  }
    
  const startWorkout = ()=> {
    let notDoneWorkout = workouts.find(item => item.isDone == false)
    props.thisCurrentWorkout(notDoneWorkout)
    props.changePage('workout')

    workouts.map(w => {
    if(!w.isDone) {
      return <WorkoutItem workouts = {w} />
    }
    })
  }
  

  return (
    <div>
      <Title text = {"Welcome Trainer"}/>
      <button className='workoutButton largeBtn blueBG' onClick={startWorkout}>start</button> <br />
      {workouts.map(w => <WorkoutItem workouts = {w}/>)} 
    </div>
  )
}
