import React, {useState} from 'react'
import Kinder from './Kinder'
import Title from './Title'
import Option from './Option'

export default function Products(props) {

  const [choosenProduct, setChoosenProduct] = useState(0)

  const changeProductDispaly = (event) => setChoosenProduct(event.target.value);

  return (
   
    <div className='productBox'>
      <Title text={"Kinder products"} />
      <select onChange={changeProductDispaly}>
        {props.kinders.map((kinder, index) => {
          return <Option text={kinder.productName} index={index}/>
        })}
      </select>
      <Kinder kinder={props.kinders[choosenProduct]} />
</div>
  )
}
