import React, {  useState } from 'react';
import Forecast from './Forecast';

export default function FavoritePage(props) {

  const API_KEY = 'AMn2HUVjVbYVX21t7LFEXOQMHW5cpMlS'
  // const API_KEY = 'mf7ATNQVNyhudAntt7XxOxAyaRcVAS3K'
  // const API_KEY = 'WebHzrX0S4ZGR1xqMmgg9s1cMgcjL6CE'

  const [fav, setFav] = useState(props.favoriteLocal)
  const [currentConditions, setCurrentConditions] = useState([])

  const fetchCityCondition = (cityKey) => {
    fetch(`http://dataservice.accuweather.com/currentconditions/v1/${cityKey}?apikey=${API_KEY}`)
    .then((res)=>res.json())
    .then((data)=>setCurrentConditions([...currentConditions, data[0]]))
  }

  useEffect(()=>{
    fav.map(key => fetchCityCondition(key))
  }, [])


  return (
    <div>
      <h1>FAVV</h1>
      <div>
        {currentConditions.map(c => <Forecast  title={c.LocalizedName} tempaure={currentCondtion.Temperature.Metric.Value} />)}
      </div>
    </div>
  )
}
