import React, {useState} from 'react'
import Title from './Title'

export default function GamePage(props) {
  const [currentComp, setCurrentComp] = useState("Products")
  const [playerDeck, setPlayerDeck] = useState([])
  const [compDeck, setCompDeck] = useState([])
  const [currentCardIndex, setCurrentCardIndex] = useState(0)
  
  const [splitDeck, setSplitDeck] = useState(false)
  const [cannotHitCard, setCannotHitCard] = useState(true)

  const createDeck = () => {
    // create 52 cards from 1-13
    let cards = []
    let card = 1;
    for(let i=1; i<=52; i++) {
      cards.push(card)
      if(i%4 == 0) {
        card++
      }
    }

    let pDeck = []
    let cDeck = []
    let rnd;
    while(cards.length > 0) {
      // card for palyer
      rnd = getRnd(cards.length)
      pDeck.push(cards[rnd])
      cards.splice(rnd, 1)

      // card for computer
      rnd = getRnd(cards.length)
      cDeck.push(cards[rnd])
      cards.splice(rnd, 1)
    }
    setPlayerDeck(pDeck)
    setCompDeck(cDeck)
    setSplitDeck(true)
    setCannotHitCard(false)
  }

  const hitCard = () => {
    setCurrentCardIndex(currentCardIndex+1)
  }

  function getRnd(max) {
    return Math.floor(Math.random() * max);
  }

