    }
  }

  
  return (
    <div>
      <Title text={"WAR!"} />
      <h2>Comp</h2>
      <div>{compDeck[currentCardIndex]}</div>
      <div>{playerDeck[currentCardIndex]}</div>
      <h2>{props.playerName}</h2>
      <button disabled={splitDeck} onClick={createDeck}>Start</button>
      <button disabled={cannotHitCard} onClick={hitCard}>hit!</button><br />
      <button disabled={cannotHitCard} onClick={setCurrentComp}>Home Page</button>

    </div>
  )
}
